query:
mutation CreateSetting($accountId:Int!, $retailerInventoryCatalogue:String!,
) {
  inventoryBatchEnhancementEnabled: createSetting(input: {
		name: "INVENTORY_BATCH_ENHANCEMENT_ENABLED", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "ACCOUNT", 
		contextId: $accountId}) {
    id
    name
  }
  defaultInventoryCatalogue: createSetting(input: {
		name: "DEFAULT_INVENTORY_CATALOGUE", 
		valueType: "STRING", 
		value: $retailerInventoryCatalogue, 
		context: "ACCOUNT", 
		contextId: $accountId}) {
    id
    name
  }
  globalInventorEnabled: createSetting(input: {
		name: "GLOBAL_INVENTORY_ENABLED", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "ACCOUNT", 
		contextId: 0}) {
    id
    name
  }
   returnOrchestratedAccount: createSetting(input: {
		name: "RETURN_ORCHESTRATED", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "ACCOUNT", 
		contextId: $accountId}) {
    id
    name
  }
  InventoryBatchEnhancement:createSetting(input: {name: "INVENTORY_BATCH_ENHANCEMENT_ENABLED", valueType: "BOOLEAN", value:"true" , context: "ACCOUNT", contextId: $accountId}) {
    id
    name
    context
    contextId
    value
    valueType
  }
   ApolloClient:createSetting(input: {name: "HOTFIX_NEW_APOLLO_CLIENT_ENABLED", valueType: "BOOLEAN", value:"true" , context: "ACCOUNT", contextId: $accountId}) {
    id
    name
    context
    contextId
    value
    valueType
  }
}
