query orders($id:ID!) {
  order(id:$id){
        id
        ref
        status
   createdOn
        updatedOn
     fulfilmentChoice{
            pickupLocationRef
            deliveryType
            fulfilmentType
            fulfilmentPrice
            fulfilmentTaxPrice
            currency
            deliveryAddress{
                name
                ref
                latitude
                longitude
            }
        }
        customer{
            id
            username
            country
            lastName
            firstName
            primaryEmail
            primaryPhone
            retailer{
                id
            }
        }
        items{
            edges{
                node{
                    ref

                    quantity
                }
            }
        }
  }
}
