query:
mutation{
 createLocation(input:{
        ref:"${loc_ref}${number}"
        type:"${type}"
        name:"${name}"
        openingSchedule:{
            sunEnd:1900
            sunStart:100
            satStart:100
            satEnd:1900
            friStart:100
            friEnd:1900
            thuEnd:1900
            thuStart:100
            wedStart:100
            wedEnd:1900
            tueEnd:1900
            tueStart:100
            monEnd:1900
            monStart:100
            allHours:false
        }
        primaryAddress:{
          ref:"${ref}${number}"
          companyName:"${company}"
          name:"${name}"
          street:"KIPPAX"
          city:"ion"
          state:"NSW"
          postcode:"2010"
          region:"SURRY HILLS"
          timeZone:"AUSTRALIA"
          latitude:${latitude}
          longitude:${longitude}
        }
        retailer:{
            id:${retId}
        }
    supportPhoneNumber:"${phonenumber}"
    defaultCarrier:"${carrierName}"
    networks:{
      id:${netId}
    }
    storageAreas:{
      ref:"${ref}${number}"
      type:"DC"
    }
  }) {
    id
    ref
    type

  }
}
