mutation createOrderAndCustomer($ref:String!,$customerRef:String!, $retailerId:ID!, $firstName:String!, $lastName:String!, $email:String!) {
    HDO:createOrderAndCustomer(input:{
        ref:$ref
        type:"HD"
        totalPrice:100
        totalTaxPrice:12.0
        attributes:{
            name:"SGR Attr"
            type:"STRING"
            value:"Text value"
        }
		 retailer:{
            id:$retailerId
        }
      customer:{
        username:$customerRef
        firstName:$firstName
        primaryEmail:$email
        lastName:$lastName
        timezone:"AUD"
        promotionOptIn: true
        retailer:{
          id:$retailerId
        }
      }
        fulfilmentChoice:{
            currency:"AUD"
            deliveryInstruction:"standard"
            deliveryType:"STANDARD"
            fulfilmentPrice:100.0
            fulfilmentTaxPrice:12.0
            deliveryAddress:{
                ref:"DeliverRef"
                companyName:"test"
                name:"test"
                state:"state test"
                street:"street test"
                city:"city test"
                region:"test region"
                country:"test country"
                longitude:-30.610123
    					  latitude: 155.518998
                timeZone:"Australia/Sydney"

            }}


        items:[{
            ref:"R1PROD-1"
            productRef:"R1PROD-1"
          	productCatalogueRef:"DEFAULT:1"
                paidPrice:1000.20
                currency:"AUD"
                price:100
                taxType:"GST"
                taxPrice:10.00
                totalPrice:1010
                totalTaxPrice:100
          quantity:2
        }]
    }) {
        ref
    id
  status
  retailer{
            id
        }
        fulfilmentChoice{
            pickupLocationRef
            deliveryType
            fulfilmentType
            fulfilmentPrice
            fulfilmentTaxPrice
            currency
            deliveryAddress{
                name
                ref
                latitude
                longitude
            }
        }
        customer{
            id
            username
            country
            lastName
            firstName
            primaryEmail
            primaryPhone
            retailer{
                id
            }
        }
        items{
            edges{
                node{
                    ref
                    quantity
                }
            }
        }
  }
}
