query:
mutation CreateSetting($retailerId:Int!,
$returnReasonValue:Json,
$returnConditionValue:Json,
$taxTypeValue:Json) {
  waveRubixified: createSetting(input: {
		name: "WAVE_RUBIXIFIED", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
  returnOrchestrated: createSetting(input: {
		name: "RETURN_ORCHESTRATED", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
  returnReason: createSetting(input: {
		name: "RETURN_REASON", 
		valueType: "JSON", 
		lobValue: $returnReasonValue,
		context: "RETAILER", 
		contextId: $retailerId}) {
    id
    name
  }
  returnCondition: createSetting(input: {
		name: "RETURN_CONDITION", 
		valueType: "JSON", 
		lobValue: $returnConditionValue,
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
  taxType: createSetting(input: {
		name: "DEFAULT_TAX_TYPE", 
		valueType: "JSON", 
		lobValue: $taxTypeValue,
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
  defaultReturnDestinationLocation: createSetting(input: {
		name: "DEFAULT_RETURN_DESTINATION_LOCATION", 
		valueType: "STRING", 
		value: "OGS1-1", 
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
  returnOrderType: createSetting(input: {
		name: "RETURN_ORDER_TYPE", 
		valueType: "STRING", 
		value: "DEFAULT", 
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
  rubixEnabled: createSetting(input: {
		name: "RUBIX_ENABLED", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
  orderAddressValidationRequired: createSetting(input: {
		name: "ORDER_ADDRESS_VALIDATION_REQUIRED", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
  compatibilityEventEnabled: createSetting(input: {
		name: "COMPATIBILITY_EVENT_ENABLED", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
  fetchUserCoordinate: createSetting(input: {
		name: "FETCH_USER_COORDINATE", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "RETAILER", 
		contextId: $retailerId}) {
    id
    name
  }
  articleCreateAfterPack: createSetting(input: {
		name: "ALLOW_CREATE_ARTICLE_AFTER_PACK", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "RETAILER", 
		contextId: $retailerId}) {
    id
    name
  }

  globalInventorEnabled: createSetting(input: {
		name: "GLOBAL_INVENTORY_ENABLED", 
		valueType: "BOOLEAN", 
		value: "true", 
		context: "RETAILER", 
		contextId: ${retId}}) {
    id
    name
  }
}
